    e.preventDefault();
    alert("Insert")
  }

  return (
    <div className="container">
      <button className="btn">Add</button>
      <form onSubmit ={sendData}>
        <div className="mb-3">
            <label for="name" className="form-label">name</label>
             <input type="text" className="form-control" id="formGroupExampleInput2" placeholder="name" onChange={handleOnChange}/>
          </div>
          <div className="mb-3">
            <label for="age" className="form-label">age</label>
            <input type="text" className="form-control" id="formGroupExampleInput2" placeholder="age" onChange={handleOnChange}/>
          </div>
          <div className="mb-3">
            <label for="age" className="form-label">gender</label>
            <input type="text" className="form-control" id="formGroupExampleInput2" placeholder="gender" onChange={handleOnChange}/>
          </div>
          <button type="submit">Submit</button>
          </form>
      </div>



  );
}

export default App;
