import './App.css';
import {useEffect, useState} from 'react';
import axios from 'axios';


axios.defaults.baseURL = "http://localhost:3000/"


function App() {

 // const [addSection, setAddSection] = useState(false)

  const [data, setData] = useState({
    name : "",
    age : "",
    gender : "",
  })

  const handleOnChange = (e) =>{
    const {value,name} = e.target
    setData((preve)=>{
      return{
        ...preve,
        [name] : value
      }


    })


  }

  const [dataList, setDataList] = useState([]);

  const handleSubmit = async(e)=>{
    e.preventDefault()
    await axios.post("http://localhost:8070/test/add", data).then(() => {
      alert("Student Added")

    }).catch((err) =>{
      alert(err)
    })

  }


  const getFetchData = async()=>{
    await axios.get("http://localhost:8070/test/", data).then(() => {
    setDataList(data.data.data)
  }).catch((err) =>{
      alert(err)
    })
  }

  useEffect(()=>{
    getFetchData()
  },[])

  console.log(dataList)

  return (
    <div className="container">
      <button className="btn">Add</button>
      <form onSubmit ={handleSubmit}>
        <div className="mb-3">
            <label for="name" className="form-label">name</label>
             <input type="text" className="form-control"  name="name" placeholder="name" onChange={handleOnChange}/>
          </div>
          <div className="mb-3">
            <label for="age" className="form-label">age</label>
            <input type="text" className="form-control" name="age" placeholder="age" onChange={handleOnChange}/>
          </div>
          <div className="mb-3">
            <label for="age" className="form-label">gender</label>
            <input type="text" className="form-control"  name="gender" placeholder="gender" onChange={handleOnChange}/>
          </div>
          <button type="submit">Submit</button>
          </form>
      </div>



  );
}

export default App;
