import './App.css';
import {useEffect, useState} from 'react';
import axios from 'axios';
import Form from "./components/Form";


axios.defaults.baseURL = "http://localhost:3000/"


function App() {

 // const [addSection, setAddSection] = useState(false)

  const [editSection, setEditSection] = use
  const [data, setData] = useState({
    name : "",
    age : "",
    gender : "",
  })

  const handleOnChange = (e) =>{
    const {value,name} = e.target
    setData((preve)=>{
      return{
        ...preve,
        [name] : value
      }


    })


  }


  //add data
  const handleSubmit = async(e)=>{
    e.preventDefault()
    await axios.post("http://localhost:8070/test/add", data).then(() => {
      alert("Student Added")
      getFetchData()
    }).catch((err) =>{
      alert(err)
    })

  }

 //get data
  const [dataList, setDataList] = useState([]);

  const getFetchData = async()=>{
    await axios.get("http://localhost:8070/test/").then((res) => {
      setDataList(res.data);
  }).catch((err) =>{
      alert(err)
    })
  }

  useEffect(()=>{
    getFetchData();
  },[])


  //edit data
  const handleUpdate = async(id) => {

  }


  //delete data
  const handleDelete = async(id) => {
    await axios.delete("http://localhost:8070/test/delete/"+id).then(() => {
      alert("Successfully Delete")
      getFetchData()

    })

  }

  return (
    <>
    <div className="container">
      <button className="btn">Add</button>
      {
        <Form
          handleSubmit = {handleSubmit}
          handleOnChange = {handleOnChange}
        />
      }

      <div className='table table-striped'>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Name</th>
            <th scope="col">Age</th>
            <th scope="col">Gender</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {
            dataList[0]? (
              dataList.map((e1) =>{
                return(
                  <tr>
                    <td>{e1.name}</td>
                    <td>{e1.age}</td>
                    <td>{e1.gender}</td>
                    <td>
                       <button className='btn btn-edit'>Edit</button>
                       <button className='btn btn-delete' onClick = {()=>handleDelete(e1._id)}>Delete</button>
                    </td>
                  </tr>
                )
              })
            )
            :(
              <p>No Data</p>
            )

          }


        </tbody>
      </table>

      </div>

    </div>



</> 
  );
}

export default App;
