import './App.css';
import {useState} from 'react';

function App() {

 // const [addSection, setAddSection] = useState(false)
 const [data, setData] = useState({
  name : "",
  age : "",
  gender : "",
 })

  const handleOnChange = (e) =>{
    const {value,name} = e.target
    setData((preve)=>{
      return{
        ...preve,
        [name] : value
      }


    })
  }

  const handleSubmit = (e)=>{
    e.preventDefault()
  }





  return (
    <div className="container">
      <btn className="btn">Add</btn>
        <div class="mb-3">
            <label for="name" class="form-label">name</label>
            <input type="text" class="form-control" id="formGroupExampleInput2" placeholder="name" onChange={handleOnChange}/>
          </div>
          <div class="mb-3">
            <label for="age" class="form-label">age</label>
            <input type="text" class="form-control" id="formGroupExampleInput2" placeholder="age" onChange={handleOnChange}/>
          </div>
          <div class="mb-3">
            <label for="age" class="form-label">gender</label>
            <input type="text" class="form-control" id="formGroupExampleInput2" placeholder="gender" onChange={handleOnChange}/>
          </div>
          <button type="submit">Submit</button>
        </div>



  );
}

export default App;
